mapper.select:
select id, is_admin, name
from t_user
where id = #{id}

select id, token, use_token, count
from t_user
where id = #{id}

select id
from t_user 
where name = #{name}

select pwd
from t_user 
where id = #{id}

mapper.update:
update t_user set
name = #{name}
where id = #{id}

update t_user set
  pwd = #{pwd}
where id = #{id}

mapper.select:
select id, pwd, is_admin, name
from t_user
where name = #{name}

select b.user_id
from t_bot b left join t_qq q on q.bot_id = b.id
where qq = #{qq}

select token
from t_user u left join t_bot b on b.user_id = u.id
where b.id = #{botId}

mapper.update:
update t_user u
 left join t_bot b on u.id = b.user_id
set token = #{num}
where b.id = #{botId}

update t_user u
 left join t_bot b on u.id = b.user_id
set use_token = #{num}
where b.id = #{botId}

update t_user u
 left join t_bot b on u.id = b.user_id
set count = #{num}
where b.id = #{botId}
